import React, {useState, Component} from 'react';
import axios from 'axios';

export const FileUploader2 = () => {
  const [files, setFiles] = useState([])

  function handleChange(event) {
    setFile(event.target.files[0])
  }

  function handleSubmit(event) {

    event.preventDefault()
    const url = '/uploadFile';
    let id = event.target.id;
    let file_reader = new FileReader();
    let file = event.target.file[0]
    file_reader.onload = () => {
      // After uploading the file
      // appending the file to our state array
      // set the object keys and values accordingly
      setFiles([...files, { file_id: id, uploaded_file: file_reader.result }]);
    };

    const formData = new FormData();
    formData.append('file', file);
    formData.append('fileName', file.name);
    formData.append("fileId", event.target.id);
    formData.append("fileId", file.id);

    const config = {
      headers: {
        'content-type': 'multipart/form-data',
      },
    };
    axios.post(url, formData, config).then((response) => {
      console.log(response.data);
    });
  }
  return(
      <div className='fileUploader2'>

              <br></br>
              <form onSubmit={handleSubmit}>
                  <p>Submit ranked gene list:&nbsp;&nbsp;<input id={1} type="file" onChange={handleChange}/></p>
                  <p>Submit gene set:&nbsp;&nbsp;<input id={2} type="file" onChange={handleChange}/></p>
                  <button type="submit">Run analysis</button>
              </form>




      </div>
  );

};
