import React, {useState, Component} from 'react';
import axios from 'axios';

export const FileUploader1 = () => {

    const [file1, setFile1] = useState()
    const [file2, setFile2] = useState()


    function handleChange1(event) {
      setFile1(event.target.files[0])
    }

    function handleChange2(event) {
      setFile2(event.target.files[0])
      console.log(file2)
    }

    function handleSubmit(event) {

      event.preventDefault()
      const url = '/uploadFile';
      const formData1 = new FormData();
      const formData2 = new FormData();

      formData1.append('file', file1);
      formData.append('fileName', file1.name);
      formData.append('file', file2);
      formData.append('fileName', file2.name);
      console.log(formData)
      const config = {
        headers: {
          'content-type': 'multipart/form-data',
        },
      };
      axios.post(url, formData, config).then((response) => {
        console.log(response.data);
      });
    }
    return(
        <div className='fileUploader1'>

                {
                //matrix.mtx, barcodes.tsv, features.tsv (genes.tsv)
                }
                <br></br>
                <form onSubmit={handleSubmit}>
                  <p>Submit 10xGenomics files:&nbsp;&nbsp;<input multiple type="file" onChange={handleChange1}/></p>
                  <p>Submit gene set:&nbsp;&nbsp;<input multiple type="file" onChange={handleChange2}/></p>
                  <button type="submit">Run analysis</button>
                </form>




        </div>
    );

};
