import React, {useState, Component} from 'react';
import axios from 'axios';

export const FileUploader1 = () => {

    const [file, setFile] = useState()
    var fileInput = document.getElementById("input_10x")
    var fileInput = document.getElementById("input_10x")


    function handleChange(event) {
      setFile(event.target.files[0])
    }

    function handleSubmit(event) {

      event.preventDefault()
      const url = '/uploadFile';
      const formData = new FormData();
      formData.append('file', file);
      formData.append('fileName', file.name);
      console.log(formData)
      const config = {
        headers: {
          'content-type': 'multipart/form-data',
        },
      };
      axios.post(url, formData, config).then((response) => {
        console.log(response.data);
      });
    }
    return(
        <div className='fileUploader1'>

                {
                //matrix.mtx, barcodes.tsv, features.tsv (genes.tsv)
                }
                <br></br>
                <form onSubmit={handleSubmit}>
                  <p>Submit 10xGenomics files:&nbsp;&nbsp;<input multiple type="file" onChange={handleChange}/></p>
                  <p>Submit gene set:&nbsp;&nbsp;<input multiple type="file" onChange={handleChange}/></p>
                  <button type="submit">Run analysis</button>
                </form>




        </div>
    );

};
