import React, {useState, Component} from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import ReactDOM from "react-dom";
import { App } from ".././App"
var listOfImages = [];

var image = ""
var element
//const imgs = [require('./../data/GSEA_Prerank/Systemic lupus erythematosus.prerank.png'), require('./../data/GSEA_Prerank/Systemic lupus erythematosus.prerank.png')]

export const FileUploader1 = () => {

    const [file1, setFile1] = useState([])
    const [file2, setFile2] = useState()
    const [images, setImages] = useState()

    //const navigate = useNavigate();


    function handleChange1(event) {
      setFile1(event.target.files)

    }

    function handleChange2(event) {
      setFile2(event.target.files[0])
      console.log(file2)
    }



    function handleSubmit1(event) {


      event.preventDefault()
      console.log(file1.length)
      console.log(file2)
      const url = '/uploadFile';
      const formData1 = new FormData();
      const formData2 = new FormData();


      formData1.append('file1', file1[0]);
      formData1.append('file2', file1[1]);
      formData1.append('file3', file1[2]);
      formData1.append("geneSetFile", file2)
      //document.getElementById('main').style.display = 'none';
      //document.getElementById('load').style.display = 'block';

      axios.post(url, formData1).then((response) => {
        console.log("Ok")
        console.log(response)

        listOfImages = []
        const obj = JSON.parse(JSON.stringify(response));


        for(var i=0;i<obj.data.files.length;i++){
            listOfImages.push(obj.data.files[i])
            console.log(obj.data.files[i])
            image = obj.data.files[i]
        }



        //THIS WORKS
        var ima = "../data/GSEA_Prerank/Systemic lupus erythematosus.prerank.png"
        setImages(ima)



        //setImages(require("/home/ernohanninen/binp29_project_v2/gsa_app/src/data/GSEA_Prerank/Systemic lupus erythematosus.prerank.png"))
        //image = "results/Staphylococcus aureus infection.prerank.png"
        //setImages(image)
        //console.log(images)


        //images = <img src={require(image)}/>
        //setImages(require(`${image}`))

        //console.log(images)


    });
  }

    return(
        <div className='fileUploader1'>

                {
                //<img src={imgs[0]}/> matrix.mtx, barcodes.tsv, features.tsv (genes.tsv)
                }

                <br></br>
                <form onSubmit={handleSubmit1}>

                  <p>Submit 10xGenomics files:&nbsp;&nbsp;<input multiple type="file" accept=".mtx, .tsv"  onChange={handleChange1}/></p>
                  <p>Submit gene set:&nbsp;&nbsp;<input multiple type="file" onChange={handleChange2}/></p>
                  <button type="submit">Run analysis</button><br></br>
                  <img src={images}/>
                </form>      

        </div>
    );

};
