import React, {useState, Component} from 'react';
import axios from 'axios';

export const FileUploader2 = () => {
  const [files, setFiles] = useState([])

  function handleSubmit(event) {
      event.preventDefault();
      const url = '/uploadFile';
      console.log(files);
      /*const formData = new FormData();
      formData.append('file', files);
      formData.append('fileName', files.name);


      const config = {
        headers: {
          'content-type': 'multipart/form-data',
        },
      };
      axios.post(url, files, config).then((response) => {
        console.log(response.data);
      });
      axios({
        method: 'post',
        url: url,
        data: {
          file: files
        }
      });
  }

  function onFileUpload(event) {

    event.preventDefault()

    let id = event.target.id;
    let file_reader = new FileReader();
    let file = event.target.files[0]
    file_reader.onload = () => {
      // After uploading the file
      // appending the file to our state array
      // set the object keys and values accordingly
      setFiles([...files, { file_id: id, uploaded_file: file_reader.result }]);
    };
    file_reader.readAsDataURL(file);
    /*
    const formData = new FormData();
    formData.append('file', file);
    formData.append('fileName', file.name);


    const config = {
      headers: {
        'content-type': 'multipart/form-data',
      },
    };*/

  }
  return(
      <div className='fileUploader2'>

              <br></br>
              <form onSubmit={handleSubmit}>
                  <div className="upload--button">
                      <p>Submit ranked gene list:&nbsp;&nbsp;<input onChange={onFileUpload}  id={1} type="file"/></p>
                  </div>
                  <div className="upload--button">
                      <p>Submit gene set:&nbsp;&nbsp;<input onChange={onFileUpload} id={2} type="file"/></p>
                  </div>

                  <button type="submit">Run analysis</button>
              </form>




      </div>
  );

};
