    formData.append("fileId", event.target.id);
    formData.append("fileId", file.id);

    const config = {
      headers: {
        'content-type': 'multipart/form-data',
      },
    };*/

  }
  return(
      <div className='fileUploader2'>

              <br></br>
              <form onSubmit={handleSubmit}>
                  <p>Submit ranked gene list:&nbsp;&nbsp;<input id={1} type="file" onChange={onFileUpload}/></p>
                  <p>Submit gene set:&nbsp;&nbsp;<input id={2} type="file" onChange={onFileUpload}/></p>
                  <button type="submit">Run analysis</button>
              </form>




      </div>
  );

};
