import React, {useState, Component} from 'react';
import axios from 'axios';

export const FileUploader1 = () => {

    const [file, setFile] = useState()
    var file_10x= document.getElementById("input_10x");
    var file_geneSet = document.getElementById("input_geneSet");
    var fileCatcher = document.getElementById("file_catcher");
    var fileList_10x = [];
    var fileList_geneSet = [];


    file_10x.addEventListener("change", function(event){
      fileList_10x = []
      for(var i = 0; i<file_10x.files.length;i++){
        fileList_10x.push(file_10x.files[i])
      }
    });

    file_geneSet.addEventListener("change", function(event){
      fileList_geneSet = []
      for(var i = 0; i<file_geneSet.files.length;i++){
        fileList_geneSet.push(file_geneSet.files[i])
      }
    });

    fileCatcher.addEventListener("submit", function(event){
      event.preventDefault()
      fileList_10x.forEach(function (file){
        sendFile(file)
      });
      fileList_geneSet.forEach(function (file){
        sendFile(file)
      });
    })

    function sendFile(file){
      var formData = new FormData();
      formData.set("file", file)
      console.log(formData)
      const config = {
        headers: {
          'content-type': 'multipart/form-data',
        },
      };
      axios.post("/file", formData, config).then((response) => {
        console.log(response.data);
      });

    }

    /*function handleSubmit(event) {

      event.preventDefault()
      const url = '/uploadFile';
      const formData = new FormData();
      formData.append('file', file);
      formData.append('fileName', file.name);
      console.log(formData)
      const config = {
        headers: {
          'content-type': 'multipart/form-data',
        },
      };
      axios.post(url, formData, config).then((response) => {
        console.log(response.data);
      });
    }*/
    return(
        <div className='fileUploader1'>

                {
                //matrix.mtx, barcodes.tsv, features.tsv (genes.tsv)
                }
                <br></br>
                <form id = "file_catcher">
                  <p>Submit 10xGenomics files:&nbsp;&nbsp;<input multiple type="file" id="input_10x"/></p>
                  <p>Submit gene set:&nbsp;&nbsp;<input multiple type="file" id="input_geneSet"/></p>
                  <button type="submit">Run analysis</button>
                </form>




        </div>
    );

};
