import React, { useState, useEffect } from 'react';

//import './App.css';
import history from './../history';

import axios from 'axios';
import { FileUploader1 } from "./components/FileUploader1"
import { FileUploader2 } from "./components/FileUploader2"

export default class Home extends Component {
    render() {
      return (
        <div className="Home">
          <div className="lander">
            <h1>Home page</h1>
            <p>A simple app showing react button click navigation</p>
            <form>
              <Button variant="btn btn-success" onClick={() => history.push('/Products')}>Click button to view products</Button>
            </form>
          </div>
        </div>
      );
    }
  }
