import React, {useState, Component} from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { App } from ".././App"


export const FileUploader1 = () => {

    const [file1, setFile1] = useState([])
    const [file2, setFile2] = useState()
    const [image, setImage] = useState()
    const [path, setPath] = useState([])
    const [files, setFiles] = useState([])
    //const navigate = useNavigate();


    function handleChange1(event) {
      setFile1(event.target.files)

    }

    function handleChange2(event) {
      setFile2(event.target.files[0])
      console.log(file2)
    }

    function getImagePaths(directory) {
      let images = [];
      directory.keys().map((item, index) => images.push(item.replace("./", "")));
      return images;
    }

    function handleSubmit(event) {

      event.preventDefault()
      console.log(file1.length)
      console.log(file2)
      const url = '/uploadFile';
      const formData1 = new FormData();
      const formData2 = new FormData();


      formData1.append('file1', file1[0]);
      formData1.append('file2', file1[1]);
      formData1.append('file3', file1[2]);
      formData1.append("geneSetFile", file2)
      //formData2.append('file4', file2);

      axios.post(url, formData1).then((response) => {
        const directory = require.context("../", false, /\.(png)$/);
        let imagePaths = getImagePaths(directory);

        let images = [];
        imagePaths.map((path) => images.push(require("../" + path)));


    });
  }

     /*axios.post(url, formData1).then(response => response.blob()).then(images => {
        image = URL.createObjectURL(images)
        console.log(image)

    }) */

      //document.getElementById('main').style.display = 'none';
      //document.getElementById('results').style.display = 'block';
      /*axios.post(url, formData2, config).then((response) => {
        console.log(response.data);
      });*/


    return(
        <div className='fileUploader1'>

                {
                //matrix.mtx, barcodes.tsv, features.tsv (genes.tsv)
                }

                <br></br>
                <form onSubmit={handleSubmit}>
                  <p>Submit 10xGenomics files:&nbsp;&nbsp;<input multiple type="file"  onChange={handleChange1}/></p>
                  <p>Submit gene set:&nbsp;&nbsp;<input multiple type="file" onChange={handleChange2}/></p>
                  <button type="submit">Run analysis</button><br></br>


                </form>      

        </div>
    );

};
